import styles from '../styles/Blog.module.css';
import Header from './header';
import { useState, useEffect } from 'react';
import axios from 'axios';

function Blog () {

  const [videos, setVideos] = useState([]);

  useEffect(() => {
    axios({
      method: 'get',
      url: `https://www.googleapis.com/youtube/v3/playlistItems?part=snippet&maxResults=20&playlistId=UUfAdLF-Ys_mxjBUHaRRjYpA&key=${process.env.YouTube_API_KEY_V3}`,
    })
    .then((res) => {
      console.log(res.data.items[0].snippet.thumbnails.standard.url);
      setVideos(res.data.items);
    })
    .catch(error => {
      console.log(error);
    });
  }, []);


  return (
    <div className={styles.container}>
      <Header />
      <h1>Blog</h1>
