import styles from '../styles/Releases.module.css';
import Header from './header';
import data from '../db/data.json';
import { useState, useEffect, useRef } from 'react';

export default function Purchase() {

  const [currentSong, setCurrentSong] = useState("");
  const audioRef = useRef(null);

  function changeSong(songName) {
    console.log(audioRef)
    audioRef.pause();
    audioRef.setAttribute('src', songName);
    audioRef.load();
    audioRef.play();
  }

  return (
    <div className={styles.container}>
      <Header />
      <h1 className={styles.releases}>Releases</h1>
      <audio ref={audioRef} controls>
        <source src={currentSong} type="audio/mp3" />
      </audio>
      <div>
        <div className={styles.albums}>
          <button onClick={() => changeSong("/Ocean.mp3")}>Ocean.wav</button>
          {/* {createAlbum("/hawaii_part_ii.jpg")}
          {createAlbum("/2020.jpg")}
          {createAlbum("/USA.jpg")}
          {createAlbum("/mystic_familiar.jpg")} */}
        </div>
      </div>
    </div>
  );
}
